public class SubStringWithConcatenationOfAllWords {

    public List<Integer> findSubstring(String s, String[] words) {

        List<Integer> result = new ArrayList();
        HashMap<String,Integer> wordsMap = new HashMap();

        int totalSubStringLength = words.length * words[0].length();
        int singleWordLength = words[0].length();

        for(String word: words){

            if(wordsMap.get(word) == null){
                wordsMap.put(word,1);
            }else{
                int currentCount = wordsMap.get(word);
                wordsMap.put(word,currentCount+1);
            }
        }

        for(int j=0; j <= s.length()-totalSubStringLength;j++){

            String firstWord = s.substring(j,j+singleWordLength);

            if(wordsMap.get(firstWord) != null){
                HashMap<String,Integer> currentMap = new HashMap();

                String testString = s.substring(j,j+totalSubStringLength);
                for(int i=0; i < totalSubStringLength; i = i+ singleWordLength){
                    String currentString = testString.substring(i,i+singleWordLength);
                    if(currentMap.get(currentString) == null){
                        currentMap.put(currentString,1);
                    }else{
                        int currentCount = currentMap.get(currentString);
                        currentMap.put(currentString,currentCount+1);
                    }
                }

                if(currentMap.equals(wordsMap)){
                    result.add(j);
                }

            }

        }


        return result;


    }

    public static void main(String[] args){
        SubStringWithConcatenationOfAllWords sub = new SubStringWithConcatenationOfAllWords();
        String string = "";
        String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
        List<Integer> results = sub.findSubstring(string,words);
        if(results.isEmpty()){
            System.out.println("Empty");
        }else{
            for(int i : results){
                System.out.print(i+" ");
            }
        }

    }
}




